package com.tns.day1;

public class FirstProgram {
	
	public static void main(String[] args) {
		// TODO Auto-generated method stub
	int a=100;
	int b=90;
	System.out.println("a+b="+(a+b));
	System.out.println("a-b="+(a-b));
	System.out.println("a/b="+(a/b));
	System.out.println("a*b="+(a*b));
	System.out.println("a%b="+(a%b));
	System.out.println("a++="+a);
	System.out.println("a--="+a);
	System.out.println("a<b="+(a<b));
	System.out.println("a>=b="+(a>=b));
	System.out.println("a<=b="+(a<=b));
	System.out.println("a<b="+(a<b));
	System.out.println("a==b="+(a==b));
	System.out.println("a=b="+(a=b));
	System.out.println("a+=b="+(a+=b));
	System.out.println("a-=b="+(a-=b));
	System.out.println("a*=b="+(a*=b));
	System.out.println("a/=b="+(a/=b));
	System.out.println("a%=b="+(a%=b));
	System.out.println((a>b)?"hi":"hello");

	}

}
